import { useState } from 'react'
import loader from "./Loader"
import { useHistory } from "react-router-dom"
import {
  Button,
  Form,
  Grid,
  Header,
  Image,
  Message,
  Segment,
} from "semantic-ui-react";

function App() {
  
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')

  async function loginUser(event) {
    event.preventDefault()

    const response = await fetch('http://localhost:1337/api/login', {
      method: 'POST',
      headers: {
        'content-type': 'application/json'
      },
      body: JSON.stringify({
        email,
        password
      }),
    })

    const data = await response.json()

    if(data.admin){
      alert('Login Successful')
      window.location.href = '/dashboard'
    } else {
      alert('Please check your Email or Password ')
    }
  }

  if (loader) {
    return (
      <Grid
        textAlign="center"
        style={{ height: '100vh'}}
        verticalAlign="middle"
      >
        <Grid.Column style={{ display: 'flex',
                              justifyContent:'center',
                              alignItems:'center', 
                              height: '90vh' }}>
          <loader />
        </Grid.Column>
      </Grid>
    );
  }
  
  return (
    <Grid textAlign="center" style={ { display: 'flex',
                                       justifyContent:'center',
                                       alignItems:'center', 
                                       height: '90vh' }} verticalAlign="middle">
      <Grid.Column style={{ maxWidth: 450 }}>
        <img src="https://i.imgur.com/kI8pQ2D.png"  alt="LOGIN-LOGO" width="300" height="300"/>
        
        <Form >
          <Segment stacked  >
             <input placeholder="Email" type="email" value={email} spellcheck="false" data-ms-editor="true"   onChange={(e) => setEmail(e.target.value)} style={{
             width: '100%',
             padding: '12px 20px',
             margin: '8px 0',
             display: 'inline-block',
             border: '1px solid',
             borderRadius: '0%',
             marginLeft: '-60px'}}
             />
            
            <input placeholder="Password.." type="password" value={password} spellcheck="false" data-ms-editor="true"  onChange={(e) => setPassword(e.target.value)} style={{
             width: '100%',
             padding: '12px 20px',
             margin: '8px 0',
             display: 'inline-block',
             border: '1px solid',
             borderRadius: '0%',
             marginLeft: '-60px'}}
             />

            <Button style={{ backgroundColor: ' #e7e7e7', border: '2px solid ', fontSize: '35px', borderRadius: '20%', marginLeft: '100px'}}
              type="submit"
              onClick={sendRequest}
            >
              Log In
            </Button>
            <span className="form-input-login">
                   <p><small>You cant Log In ? Please contact the maneger!</small></p>
            </span>
          </Segment>
        </Form>
        
      </Grid.Column>
    </Grid>
  );


    
}

export default App;
